import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import {
  FileText,
  Calendar,
  Clock,
  User,
  Upload,
  Download,
  MessageSquare,
  CheckCircle,
  AlertCircle,
  Edit,
  Save,
  X,
  MoreVertical
} from 'lucide-react';
import { format, isAfter, isBefore } from 'date-fns';
import { es } from 'date-fns/locale';

const AssignmentDetail = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const [assignment, setAssignment] = useState(null);
  const [submission, setSubmission] = useState(null);
  const [submissions, setSubmissions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [submissionContent, setSubmissionContent] = useState('');
  const [feedback, setFeedback] = useState('');
  const [points, setPoints] = useState('');

  useEffect(() => {
    fetchAssignmentData();
  }, [id]);

  const fetchAssignmentData = async () => {
    try {
      const [assignmentRes, submissionsRes] = await Promise.all([
        axios.get(`/api/assignments/${id}`),
        axios.get(`/api/assignments/${id}/submissions`)
      ]);

      setAssignment(assignmentRes.data);
      setSubmissions(submissionsRes.data);
      
      // Buscar la entrega del usuario actual
      const userSubmission = submissionsRes.data.find(
        sub => sub.student_id === user?.id
      );
      setSubmission(userSubmission);
      
      if (userSubmission) {
        setSubmissionContent(userSubmission.content || '');
      }
    } catch (error) {
      console.error('Error al cargar datos de la tarea:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const response = await axios.post(`/api/assignments/${id}/submissions`, {
        content: submissionContent
      });

      setSubmission(response.data);
      setSubmitting(false);
    } catch (error) {
      console.error('Error al enviar entrega:', error);
      setSubmitting(false);
    }
  };

  const handleGrade = async (submissionId) => {
    try {
      await axios.post(`/api/submissions/${submissionId}/grade`, {
        points_earned: points,
        feedback: feedback
      });

      // Recargar datos
      fetchAssignmentData();
      setPoints('');
      setFeedback('');
    } catch (error) {
      console.error('Error al calificar:', error);
    }
  };

  const getAssignmentStatus = () => {
    if (!assignment) return 'pending';
    
    const now = new Date();
    const dueDate = new Date(assignment.due_date);
    
    if (submission && submission.status === 'submitted') {
      return 'submitted';
    }
    
    if (now > dueDate) {
      return 'overdue';
    }
    
    if (dueDate - now < 24 * 60 * 60 * 1000) {
      return 'due-soon';
    }
    
    return 'pending';
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'submitted':
        return 'text-green-600 bg-green-100';
      case 'overdue':
        return 'text-red-600 bg-red-100';
      case 'due-soon':
        return 'text-yellow-600 bg-yellow-100';
      default:
        return 'text-secondary-600 bg-secondary-100';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'submitted':
        return <CheckCircle className="w-4 h-4" />;
      case 'overdue':
        return <AlertCircle className="w-4 h-4" />;
      default:
        return <Clock className="w-4 h-4" />;
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!assignment) {
    return (
      <div className="text-center py-12">
        <FileText className="w-16 h-16 text-secondary-400 mx-auto mb-4" />
        <h3 className="text-lg font-medium text-secondary-900 mb-2">
          Tarea no encontrada
        </h3>
        <p className="text-secondary-600 mb-4">
          La tarea que buscas no existe o no tienes acceso a ella.
        </p>
        <Link to="/assignments" className="btn-primary">
          Volver a Tareas
        </Link>
      </div>
    );
  }

  const status = getAssignmentStatus();

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Header de la Tarea */}
      <div className="card">
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-start space-x-4">
            <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center">
              <FileText className="w-6 h-6 text-blue-600" />
            </div>
            <div>
              <h1 className="text-2xl font-bold text-secondary-900 mb-2">
                {assignment.title}
              </h1>
              <p className="text-secondary-600 mb-2">
                {assignment.course?.name || 'Curso no disponible'}
              </p>
              <div className="flex items-center space-x-4 text-sm text-secondary-600">
                <div className="flex items-center">
                  <Calendar className="w-4 h-4 mr-1" />
                  {format(new Date(assignment.due_date), 'dd MMM yyyy', { locale: es })}
                </div>
                <div className="flex items-center">
                  <Clock className="w-4 h-4 mr-1" />
                  {format(new Date(assignment.due_date), 'HH:mm', { locale: es })}
                </div>
                <div className="text-sm font-medium">
                  {assignment.max_points} puntos
                </div>
              </div>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(status)}`}>
              {getStatusIcon(status)}
              <span className="ml-1 capitalize">
                {status === 'due-soon' ? 'Por vencer' : status}
              </span>
            </span>

            {user?.role === 'teacher' && (
              <button className="btn-secondary">
                <Edit className="w-4 h-4 mr-2" />
                Editar
              </button>
            )}
          </div>
        </div>

        {/* Descripción */}
        <div className="prose max-w-none">
          <h3 className="text-lg font-semibold text-secondary-900 mb-2">
            Descripción
          </h3>
          <div className="text-secondary-700 whitespace-pre-wrap">
            {assignment.description || 'Sin descripción'}
          </div>
        </div>
      </div>

      {/* Entrega del Estudiante */}
      {user?.role === 'student' && (
        <div className="card">
          <h2 className="text-lg font-semibold text-secondary-900 mb-4">
            Mi Entrega
          </h2>

          {submission ? (
            <div className="space-y-4">
              <div className="p-4 bg-secondary-50 rounded-lg">
                <div className="flex items-center justify-between mb-2">
                  <span className="text-sm font-medium text-secondary-700">
                    Estado: {submission.status}
                  </span>
                  <span className="text-sm text-secondary-600">
                    Enviado: {format(new Date(submission.submitted_at), 'dd MMM yyyy HH:mm', { locale: es })}
                  </span>
                </div>
                <div className="text-secondary-700 whitespace-pre-wrap">
                  {submission.content}
                </div>
                {submission.points_earned && (
                  <div className="mt-3 p-3 bg-green-50 rounded-lg">
                    <div className="flex items-center justify-between">
                      <span className="font-medium text-green-900">
                        Calificación: {submission.points_earned}/{assignment.max_points}
                      </span>
                      <span className="text-sm text-green-700">
                        {((submission.points_earned / assignment.max_points) * 100).toFixed(1)}%
                      </span>
                    </div>
                    {submission.feedback && (
                      <div className="mt-2 text-sm text-green-800">
                        <strong>Comentarios:</strong> {submission.feedback}
                      </div>
                    )}
                  </div>
                )}
              </div>

              {submission.status === 'draft' && (
                <form onSubmit={handleSubmit}>
                  <div className="mb-4">
                    <label className="block text-sm font-medium text-secondary-700 mb-2">
                      Actualizar Entrega
                    </label>
                    <textarea
                      value={submissionContent}
                      onChange={(e) => setSubmissionContent(e.target.value)}
                      rows={6}
                      className="input-field"
                      placeholder="Escribe tu respuesta aquí..."
                    />
                  </div>
                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      className="btn-secondary"
                    >
                      Guardar Borrador
                    </button>
                    <button
                      type="submit"
                      disabled={submitting}
                      className="btn-primary"
                    >
                      {submitting ? 'Enviando...' : 'Enviar Entrega'}
                    </button>
                  </div>
                </form>
              )}
            </div>
          ) : (
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-secondary-700 mb-2">
                  Tu Entrega
                </label>
                <textarea
                  value={submissionContent}
                  onChange={(e) => setSubmissionContent(e.target.value)}
                  rows={6}
                  className="input-field"
                  placeholder="Escribe tu respuesta aquí..."
                  required
                />
              </div>
              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  className="btn-secondary"
                >
                  Guardar Borrador
                </button>
                <button
                  type="submit"
                  disabled={submitting}
                  className="btn-primary"
                >
                  {submitting ? 'Enviando...' : 'Enviar Entrega'}
                </button>
              </div>
            </form>
          )}
        </div>
      )}

      {/* Entregas para Profesores */}
      {user?.role === 'teacher' && (
        <div className="card">
          <h2 className="text-lg font-semibold text-secondary-900 mb-4">
            Entregas de Estudiantes ({submissions.length})
          </h2>

          <div className="space-y-4">
            {submissions.map((sub) => (
              <div key={sub.id} className="p-4 bg-secondary-50 rounded-lg">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center">
                      <User className="w-4 h-4 text-primary-600" />
                    </div>
                    <div>
                      <p className="font-medium text-secondary-900">
                        {sub.student.first_name} {sub.student.last_name}
                      </p>
                      <p className="text-sm text-secondary-600">
                        {sub.student.email}
                      </p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(sub.status)}`}>
                      {sub.status}
                    </span>
                    <button className="p-2 hover:bg-secondary-200 rounded-lg">
                      <MoreVertical className="w-4 h-4 text-secondary-400" />
                    </button>
                  </div>
                </div>

                <div className="text-secondary-700 whitespace-pre-wrap mb-3">
                  {sub.content}
                </div>

                <div className="flex items-center justify-between text-sm text-secondary-600">
                  <span>
                    Enviado: {format(new Date(sub.submitted_at), 'dd MMM yyyy HH:mm', { locale: es })}
                  </span>
                  {sub.points_earned ? (
                    <span className="font-medium text-green-600">
                      {sub.points_earned}/{assignment.max_points} puntos
                    </span>
                  ) : (
                    <span className="text-secondary-500">Sin calificar</span>
                  )}
                </div>

                {!sub.points_earned && (
                  <div className="mt-3 pt-3 border-t border-secondary-200">
                    <div className="flex space-x-3">
                      <input
                        type="number"
                        placeholder="Puntos"
                        value={points}
                        onChange={(e) => setPoints(e.target.value)}
                        className="input-field w-24"
                        min="0"
                        max={assignment.max_points}
                      />
                      <input
                        type="text"
                        placeholder="Comentarios (opcional)"
                        value={feedback}
                        onChange={(e) => setFeedback(e.target.value)}
                        className="input-field flex-1"
                      />
                      <button
                        onClick={() => handleGrade(sub.id)}
                        className="btn-primary"
                      >
                        <Save className="w-4 h-4 mr-1" />
                        Calificar
                      </button>
                    </div>
                  </div>
                )}

                {sub.feedback && (
                  <div className="mt-3 p-3 bg-blue-50 rounded-lg">
                    <p className="text-sm text-blue-800">
                      <strong>Comentarios:</strong> {sub.feedback}
                    </p>
                  </div>
                )}
              </div>
            ))}
          </div>

          {submissions.length === 0 && (
            <div className="text-center py-8">
              <FileText className="w-12 h-12 text-secondary-400 mx-auto mb-4" />
              <p className="text-secondary-600">No hay entregas aún</p>
            </div>
          )}
        </div>
      )}

      {/* Comentarios */}
      <div className="card">
        <h2 className="text-lg font-semibold text-secondary-900 mb-4">
          Comentarios
        </h2>
        <div className="space-y-4">
          <div className="flex space-x-3">
            <div className="w-8 h-8 bg-primary-100 rounded-full flex items-center justify-center">
              <User className="w-4 h-4 text-primary-600" />
            </div>
            <div className="flex-1">
              <textarea
                placeholder="Agregar un comentario..."
                rows={3}
                className="input-field"
              />
              <div className="flex justify-end mt-2">
                <button className="btn-primary">
                  <MessageSquare className="w-4 h-4 mr-2" />
                  Comentar
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssignmentDetail;
